import React from 'react';

import Input from '../../UI/Input';
import styles from './BudItemForm.module.css';

const BuditemForm = () => {
  return (
    <form className={styles.form}>
      <Input
        formLabel="amount"
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
    </form>
  );
};

export default BuditemForm;
