import React from 'react';

import Card from '../UI/Card';
import styles from './AvailableBuds.module.css';
import BudItem from './BudItem/BudItem';

const BUDS_SELECTION = [
  {
    id: "b1",
    name: "Purple Kush (20% THC)",
    description: "Everything is purple...",
    price: 900,
  },
  {
    id: "b2",
    name: "Dr.GreenThumb (16%)",
    description:
      "15% THC, slightly sticky and that oh so pinty-inty smell after burn",
    price: 700,
  },
  {
    id: "b3",
    name: "Onyxial Piff (19% THC)",
    description: "Relax, take a load off... inhale and fly.",
    price: 800,
  },
  {
    id: "b4",
    name: "UshyGushyKushy (25% THC)",
    description:
      "You have nothing productive to do today and ready to touch the sky",
    price: 1200,
  },
];

const AvailableBuds = () => {
  const budsList = BUDS_SELECTION.map((bud) => (
    <BudItem
      key={bud.id}
      title={bud.name}
      price={bud.price}
      description={bud.description}
    />
  ));
  return (
    <section className={styles.buds}>
      <Card>
        <ul>{budsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableBuds;
